const lambda = require("aws-cdk-lib/aws-lambda");
const { Construct } = require("constructs");
const path = require("path");

const iam = require("aws-cdk-lib/aws-iam");

const { Duration } = require("aws-cdk-lib");

class SsmParameterGet extends Construct {
  functionArn;
  function;

  constructor(scope, id, props) {
    super(scope, id);

    const role = new iam.Role(this, "SsmParameterGetRole", {
      assumedBy: new iam.ServicePrincipal("lambda.amazonaws.com"),
      managedPolicies: [
        iam.ManagedPolicy.fromAwsManagedPolicyName(
          "service-role/AWSLambdaBasicExecutionRole",
        ),
      ],
      inlinePolicies: {
        SsmParameterGet: new iam.PolicyDocument({
          statements: [
            new iam.PolicyStatement({
              actions: [
                "ssm:DescribeParameters",
                "ssm:GetParameter",
                "ssm:GetParameters",
              ],
              resources: "*",
            }),
          ],
        }),
      },
    });

    this.function = new lambda.Function(this, "SsmParameterGet", {
      role: role,
      runtime: lambda.Runtime.RUBY_3_2,
      code: lambda.Code.fromAsset(path.join(__dirname, "../lambdas")),
      handler: "ssm_parameters_get.SsmParametersGet.handler",
      layers: props.layers,
      timeout: Duration.seconds(120),
      environment: {
        GEM_PATH: "/opt/ruby/3.2.0",
      },
    });

    this.functionArn = this.function.functionArn;
  }
}

module.exports = { SsmParameterGet };

