      "id": "6457d0fa-2690-4fc3-95fd-15e749dbe53a",
      "agentId": "main",
      "name": "TRyz Comedy Event Check",
      "enabled": true,
      "createdAtMs": 1770196106725,
      "updatedAtMs": 1771027200001,
      "schedule": {
        "kind": "cron",
        "expr": "0 9 * * *",
        "tz": "Asia/Tokyo"
